create table if not exists quartzjobinfo (rootid bigint NOT NULL AUTO_INCREMENT PRIMARY KEY, timecreated bigint DEFAULT 0, timeupdated bigint DEFAULT 0, active BOOL DEFAULT TRUE, modifiedby bigint DEFAULT 0, createdby bigint DEFAULT 0, version bigint DEFAULT 0, tenantid bigint NOT NULL, jobname varchar(64), jobgroup varchar(64), jobstatus varchar(16), isrecurring BOOL, errorinfo varchar(2048), UNIQUE KEY `quartz_job_id` (`jobname`,`jobgroup`));