create table if not exists notification (rootid bigint NOT NULL AUTO_INCREMENT PRIMARY KEY, timecreated bigint DEFAULT 0, timeupdated bigint DEFAULT 0, active BOOL DEFAULT TRUE, modifiedby bigint DEFAULT 0, createdby bigint DEFAULT 0, version bigint DEFAULT 0, tenantid bigint NOT NULL, userid bigint NOT NULL, content varchar(512), redirectpath varchar(128), notificationread BOOL, notificationtype int);