/*tasks*/
create table if not exists task (rootid bigint NOT NULL AUTO_INCREMENT PRIMARY KEY, tenantid bigint NOT NULL, active BOOL DEFAULT TRUE, timecreated bigint DEFAULT 0, timeupdated bigint DEFAULT 0, modifiedby bigint DEFAULT 0, createdby bigint DEFAULT 0, version bigint DEFAULT 0, title varchar(64), description varchar(512), tasktype varchar(24), status varchar(16), ownerid bigint, startdate datetime, enddate datetime, broadcast BOOL, CONSTRAINT FOREIGN KEY(tenantid) REFERENCES tenant(rootid),  CONSTRAINT FOREIGN KEY(ownerid) REFERENCES employee(rootid));
create table if not exists taskassignee (rootid bigint NOT NULL AUTO_INCREMENT PRIMARY KEY, tenantid bigint NOT NULL, active BOOL DEFAULT TRUE, timecreated bigint DEFAULT 0, timeupdated bigint DEFAULT 0, modifiedby bigint DEFAULT 0, createdby bigint DEFAULT 0, version bigint DEFAULT 0, status varchar(16), assigneeid bigint, taskid bigint, completedon datetime, CONSTRAINT FOREIGN KEY(tenantid) REFERENCES tenant(rootid), CONSTRAINT FOREIGN KEY(assigneeid) REFERENCES employee(rootid), CONSTRAINT FOREIGN KEY(taskid) REFERENCES task(rootid));
create table if not exists taskdocument (rootid bigint NOT NULL AUTO_INCREMENT PRIMARY KEY, tenantid bigint NOT NULL, active BOOL DEFAULT TRUE, timecreated bigint DEFAULT 0, timeupdated bigint DEFAULT 0, modifiedby bigint DEFAULT 0, createdby bigint DEFAULT 0, version bigint DEFAULT 0, filestoreid bigint, taskid bigint, CONSTRAINT FOREIGN KEY(filestoreid) REFERENCES filestore(rootid), CONSTRAINT FOREIGN KEY(taskid) REFERENCES task(rootid), CONSTRAINT FOREIGN KEY(tenantid) REFERENCES tenant(rootid));